let rt=-1,ii=-1;
function countdown(n){
	let i=document.getElementById("countdown_full");
	i.style.visibility="visible";
	let e=document.getElementById("countdown");
	e.onclick="",console.log(rt),rt>0?(e.innerHTML=rt,rt--):(clearInterval(ii),i.style.visibility="hidden",setTimeout(n,0))
	}
	
function count(n,i){
	rt=i,countdown(n),ii=setInterval(function(){countdown(n)},1e3)}
